      const b = branch.concat(v)
      const [kfs, kr] = Key.check(k, b, p)

      if (kfs) {
        failures.push(...kfs)
      } else {
        const [vfs, vr] = Value.check(v, b, p)

        if (vfs) {
          failures.push(...vfs)
        } else {
          result[kr] = vr
