import { Struct, Superstruct } from '..'

export const createScalar = (
  schema: string,
  defaults: any,
  struct: Superstruct
): Struct => {
  invariant(
    typeof schema === 'string',
    `Scalar structs must be defined as a string, but you passed: ${schema}`
  )

  const { Types } = struct

  invariant(
    schema in Types,
    `No struct validator function found for type "${schema}".`
  )

  const Struct = struct(Types[schema], defaults)
  Struct.kind = 'scalar'
  Struct.type = schema
  return Struct
}
import { isStruct, Struct, Superstruct } from '..'

export const createShorthand = (
  schema: any,
  defaults: any,
  struct: Superstruct
): Struct => {
  if (isStruct(schema)) {
    return schema
  }

  if (Array.isArray(schema)) {
    if (schema.length === 1) {
      const [first] = schema
      return struct.array([first], defaults)
    } else if (schema.length > 1) {
      return struct.tuple(schema, defaults)
    }
  }

  if (typeof schema === 'function') {
    return struct.function(schema, defaults)
  }

  if (typeof schema === 'object') {
    return struct.object(schema, defaults)
  }

  if (typeof schema === 'string') {
    let optional = false
    let Struct

    if (schema.endsWith('?')) {
      optional = true
      schema = schema.slice(0, -1)
    }

    if (schema.includes('|')) {
      const scalars = schema.split(/\s*\|\s*/g)
      Struct = struct.union(scalars, defaults)
    } else if (schema.includes('&')) {
      const scalars = schema.split(/\s*&\s*/g)
      Struct = struct.intersection(scalars, defaults)
    } else {
      Struct = struct.scalar(schema, defaults)
    }

    if (optional) {
      Struct = struct.union([Struct, 'undefined'], undefined)
    }

    return Struct
  }

  throw new Error(
    `A schema definition must be an object, array, string or function, but you passed: ${schema}`
  )
}
import invariant from 'tiny-invariant'
import { Branch, Failure, Path, Struct, Superstruct } from '..'
import { createStruct } from '../struct'

export const createTuple = (
  schema: any[],
  defaults: any,
  struct: Superstruct
): Struct => {
  invariant(
    Array.isArray(schema),
    `Tuple structs must be defined as an array, but you passed: ${schema}`
  )

  const Elements = schema.map(s => struct(s))
  const Struct = createStruct({
    kind: 'tuple',
    type: `[${Elements.map(S => S.type).join()}]`,
    defaults,
    struct,
  })

  Struct.check = (
    value: any = Struct.default(),
    branch: Branch,
    path: Path
  ): [Failure[]?, any?] => {
    if (!Array.isArray(value)) {
      return [[Struct.fail({ value, branch, path })]]
    }

    const result = []
    const failures: Failure[] = []
    const length = Math.max(value.length, Elements.length)

    for (let i = 0; i < length; i++) {
      const Element = Elements[i]
      const v = value[i]
      const p = path.concat(i)
      const b = branch.concat(v)

      if (!Element) {
        failures.push(
          Struct.fail({ type: undefined, value: v, path: p, branch: b })
        )
      } else {
        const [efs, er] = Element.check(v, b, p)

        if (efs) {
          failures.push(...efs)
        } else {
          result[i] = er
        }
      }
    }

    return failures.length ? [failures] : [undefined, result]
  }

  return Struct
}
import invariant from 'tiny-invariant'
import { createStruct } from '../struct'
import { Branch, Failure, Path, Struct, Superstruct } from '..'

export const createUnion = (
  schema: any[],
  defaults: any,
  struct: Superstruct
): Struct => {
  invariant(
    Array.isArray(schema) && schema.length !== 0,
    `Union structs must be defined as a non-empty array, but you passed: ${schema}`
  )

  const Structs = schema.map(sch => struct(sch))
  const type = Structs.map(s => s.type).join(' | ')
  const Struct = createStruct({
    kind: 'union',
    type,
    defaults,
    struct,
  })

  Struct.check = (
    value: any = Struct.default(),
    branch: Branch,
    path: Path
  ): [Failure[]?, any?] => {
    for (const struct of Structs) {
      const [fs, v] = struct.check(value, branch, path)

      if (!fs) {
        return [undefined, v]
      }
    }

    return [[Struct.fail({ value, branch, path })]]
  }

  return Struct
}
import { Validator, Types as BuiltinTypes } from './types'
import { Branch, Failure, Path, StructError } from './struct-error'
import { Struct } from './struct'
import {
  createArray,
  createDynamic,
  createEnum,
  createFunction,
  createInstance,
  createInterface,
  createIntersection,
  createLazy,
  createLiteral,
  createObject,
  createPartial,
  createPick,
  createRecord,
  createScalar,
  createShorthand,
  createSize,
  createTuple,
  createUnion,
} from './structs'

/**
 * `SuperstructSettings` are passed in when creating a `Superstruct` factory.
 */

export type SuperstructSettings = {
  types: Record<string, Validator>
  error: { new (failures: Failure[]): Error }
}

/**
 * Create a struct singleton with settings that include your own domain-specific
 * data `types`, and an optional custom `Error` class.
 */

export const superstruct = (
  settings: Partial<SuperstructSettings> = {}
): Superstruct => {
  const struct = (schema: any, defaults?: any): Struct => {
    return createShorthand(schema, defaults, struct)
  }

  struct.array = (schema: [any], defaults?: any): Struct => {
    return createArray(schema, defaults, struct)
  }

  struct.dynamic = (
    schema: (value: any, branch: Branch, path: Path) => Struct,
    defaults?: any
  ): Struct => {
    return createDynamic(schema, defaults, struct)
  }

  struct.enum = (schema: any[], defaults?: any): Struct => {
    return createEnum(schema, defaults, struct)
  }

  struct.function = (schema: Validator, defaults?: any): Struct => {
    return createFunction(schema, defaults, struct)
  }

  struct.instance = (schema: any, defaults?: any): Struct => {
    return createInstance(schema, defaults, struct)
  }

  struct.interface = (schema: any, defaults?: any): Struct => {
    return createInterface(schema, defaults, struct)
  }

  struct.intersection = (schema: any[], defaults?: any): Struct => {
    return createIntersection(schema, defaults, struct)
  }

  struct.lazy = (schema: () => Struct, defaults?: any): Struct => {
    return createLazy(schema, defaults, struct)
  }

  struct.literal = (schema: any, defaults?: any): Struct => {
    return createLiteral(schema, defaults, struct)
  }

  struct.object = (schema: {}, defaults?: any): Struct => {
    return createObject(schema, defaults, struct)
  }

  struct.optional = (schema: any, defaults?: any): Struct => {
    return createUnion([schema, 'undefined'], defaults, struct)
  }

  struct.partial = (schema: {}, defaults?: any): Struct => {
    return createPartial(schema, defaults, struct)
  }

  struct.pick = (schema: {}, defaults?: any): Struct => {
    return createPick(schema, defaults, struct)
  }

  struct.record = (schema: [any, any], defaults?: any): Struct => {
    return createRecord(schema, defaults, struct)
  }

  struct.scalar = (schema: string, defaults?: any): Struct => {
    return createScalar(schema, defaults, struct)
  }

  struct.size = (schema: [number, number], defaults?: any): Struct => {
    return createSize(schema, defaults, struct)
  }

  struct.tuple = (schema: any[], defaults?: any): Struct => {
    return createTuple(schema, defaults, struct)
  }

  struct.union = (schema: any[], defaults?: any): Struct => {
    return createUnion(schema, defaults, struct)
  }

  struct.Error = settings.error || StructError
  struct.Types = { ...BuiltinTypes, ...settings.types }
  return struct
}

/**
 * `Superstruct` factories create different kinds of [[Struct]] validators, and
 * encapsulate the user-defined data types.
 *
 * The [[struct]] export is a factory that ships with Superstruct by default,
 * pre-configured with all of the built-in JavaScript data types. It's the
 * easiest way to quickly define structs:
 *
 * ```js
 * import { struct } from 'superstruct'
 *
 * const User = struct({
 *   id: 'number',
 *   name: 'string',
 * })
 * ```
 *
 * If you need to define custom data types, you can define your own by using
 * the [[superstruct]] export:
 *
 * ```js
 * import { superstruct } from 'superstruct'
 * import isEmail from 'is-email'
 * import isUrl from 'is-url'
 *
 * const struct = superstruct({
 *   types: {
 *     email: value => isEmail(value) && value.length < 256,
 *     url: value => isUrl(value) && value.length < 2048,
 *   }
 * })
 *
 * const User = struct({
 *   id: 'number',
 *   name: 'string',
 *   email: 'email',
 *   website: 'url?',
 * })
 * ```
 *
 * This way you can easily define structs that contain types like `'email'`,
 * `'url'`, or whatever else your application may need.
 */

export interface Superstruct {
  /**
